use alohomora::pcr::{PrivacyCriticalRegion, Signature};

fn main() {
    copy_fn_to_dependencies();
    copy_between_fns();
  
}

fn copy_between_fns() {
    let _pcr = PrivacyCriticalRegion::new(|x: u8| { x + 1  },
        Signature {username: "corinnt", 
            signature: ""}, 
        Signature {username: "corinnt", 
            signature: ""},  
        Signature {username: "corinnt", 
            signature: ""}); 

    let _pcr2 = PrivacyCriticalRegion::new(|x: u8| { 
                //copy a fn review signature to different PCR
                x + 2 },
                Signature {username: "corinnt", // copied signature from _pcr
                    signature: ""}, 
                Signature {username: "corinnt",  
                    signature: ""},             
                Signature {username: "corinnt", 
                    signature: ""});
}

fn copy_fn_to_dependencies() {
    let _pcr = PrivacyCriticalRegion::new(|x: u8| { 
        //copy fn sign to dependencies signature
        x + 1  },
        Signature {username: "corinnt", 
            signature: ""}, 
        Signature {username: "corinnt", 
            signature: ""},  
        Signature {username: "corinnt", 
            signature: ""}); 
}